xwikidoc.content:
{{groovy}}
import org.xwiki.scripting.documentation.ScriptBindingsFinder;

def getBindingDocLink(def binding) {
  def docLink = binding.docLink;
  if (docLink) {
    return "[[${binding.type.simpleName}>>$docLink]]";
  } else {
    return binding.type.simpleName
  }
}

def getModuleDocLink(def module) {
  if (!module) {
    return " ";
  }
  def moduleDocLink = module.docLink;
  if (moduleDocLink) {
    return "[[${module.id}>>$moduleDocLink]]";
  } else {
    return module.id
  }
}

def getLabel(def check, def word, def style) {
  if (check) {
    return "(% class=\"bg-$style\" style=\"text-align: center\" %)**$word**";
  } else {
    return " "
  }
}

def getDescription(def binding) {
  def desc = binding.description;
  if (desc) {
    return services.localization.render(desc);
  } else {
    return " ";
  }
}

xwiki.ssfx.use("js/xwiki/table/table.css");
xwiki.jsfx.use("js/xwiki/table/tablefilterNsort.js", true);

ScriptBindingsFinder bf = services.component.getComponentManager().getInstance(ScriptBindingsFinder.class);
println '(% class="grid sortable filterable" id="tableid" %)';
println '(% class="sortHeader" %)|= Binding Kind |= Binding Name |= Binding Class |= Description |= Deprecated |= Legacy |= Internal |= Binding Module';
bf.find().sort(){ it.fullName.toLowerCase() }.each() {
println '|' + it.kind + '|' + it.fullName + '|' + getBindingDocLink(it)+ '|' + getDescription(it) + '|' + getLabel(it.isDeprecated(),"Deprecated","info") + '|' + getLabel(it.isLegacy(),"Legacy","warning") + '|' + getLabel(it.isInternal(),"Internal","danger") + '|' + getModuleDocLink(it.resource);
}
{{/groovy}}
